import java.util.*; class Student{ String name; int age; String grade;} public class Day5_StudentInfoSystem { public static void main(String[] args){ Scanner sc=new Scanner(System.in); Student s=new Student(); System.out.print("Name: "); s.name=sc.nextLine(); System.out.print("Age: "); s.age=sc.nextInt(); sc.nextLine(); System.out.print("Grade: "); s.grade=sc.nextLine(); System.out.println("Student: "+s.name+" Age: "+s.age+" Grade: "+s.grade); }}